// pages/index.js
// This is the Link API
import React from 'react'
import Link from 'next/link'


const href = {
  pathname: '/about',
  query: { name: 'next' }
}

const as = {
  pathname: '/about/next',
  hash: 'title-1'
}

const Index = () => (
  <div>
    <h1>Home page</h1>

    <p><Link href={href} as={as}>
      <a>Go to /about/next</a>
    </Link></p>

    <p><Link href="/about">
      <a title="About Page">About Page</a>
    </Link></p>

    <p><Link href="/about">
      <button>Go to About Page</button>
    </Link></p>

    <p>Hello Next.js</p>
  </div>
)

export default Index
